mapper.delete:
delete
from t_forum_users
where forums_id = #{forumId}
  and users_id = #{userId}

mapper.update:
update t_forum
set blog_number = blog_number + 1
where id = #{id}

update t_forum
set people_number = people_number + 1
where id = #{forumId}

update t_forum
set blog_number = blog_number - 1
where id = #{id}

update t_forum
set popular = popular + 100
where id = #{id}
